import axios from "axios";
import { useEffect } from "react";
import { CircleLoader } from "react-spinners";
import {connect} from 'react-redux'
import { fetchCart, removeFromCart, removeOneFromCart } from "../reduxstore";

function Cart({cart, loading, fetchCart, removeFromCart, removeOneFromCart}) {
    const loaderStyle = {
        position:"absolute",
        left:"50%",
        top:"50%",
        zIndex:"999",
    }
    let total = 0;
    useEffect(() => {
        fetchCart()
    },[]);

    return (
        <div>
            {loading ? <div style={loaderStyle}><CircleLoader/></div>: <table className="table" style={{width:"75%"}}>
                <thead>
                    <tr>
                        <th>Image</th>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        cart.map((item, index) => {
                            total += item.price * item.quantity
                            return (
                                <tr key={index}>
                                    <td>
                                        <img 
                                        src={item.image} 
                                        alt="cake img" 
                                        style={{width:"60px",height:"60px"}}
                                        />
                                    </td>
                                    <td>{item.name}</td>
                                    <td>${item.price}</td>
                                    <td>{item.quantity}</td>
                                    <td>
                                        <button 
                                        className="btn btn-danger" 
                                        onClick={() => removeFromCart(item.cakeid)}
                                        >
                                            Remove from cart
                                        </button>
                                        {item.quantity > 1 && <button 
                                        className="btn btn-warning" 
                                        style={{marginLeft:"5px"}}
                                        onClick={() => removeOneFromCart(item.cakeid)}
                                        >
                                            Remove only 1 from the cart
                                        </button>}
                                    </td>

                                </tr>
                            )
                        })
                    }
                </tbody>
                <tfoot style={{background:"#ededed"}}>
                    <tr>
                        <th colspan="3">Total:</th>
                        <td>${total}</td>
                    </tr>
                </tfoot>
            </table>}
        </div>
    )
}
const mapStateToProps = (state) => {
    return {
        loading:state.cart.loading,
        cart:state.cart.cart
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        fetchCart:() => dispatch(fetchCart()),
        removeFromCart: (data) => dispatch(removeFromCart(data)),
        removeOneFromCart: (data) => dispatch(removeOneFromCart(data))
    }
}


export default connect(mapStateToProps,mapDispatchToProps)(Cart);
