import {BrowserRouter, Route, Switch, Redirect} from 'react-router-dom';
import { useState } from 'react';
import Signup from './Signup';
import Home from './Home';
import Navbar from './Navbar';
import PageNotFound from './PageNotFound';
import Login from './Login';
import Cart from './Cart';
import Forgot from './Forgot';
import Checkout from './Checkout';
import Admin from './Admin';
import AddCake from './AddCake';
import CakeDetails from './CakeDetails';
function NewApp() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [url, setUrl] = useState(`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/allcakes`);
    return (
        <div>
            <BrowserRouter>
                <Navbar isLoggedIn={isLoggedIn} setUrl={setUrl}/>
                <Switch>
                    <Route exact path="/">
                        {!isLoggedIn ? <Redirect to="/login"/>: <Home url={url}/>}
                    </Route>
                    <Route exact path="/signup" component={Signup}></Route>
                    <Route exact path="/login">
                        {isLoggedIn ? <Redirect to="/"/> : <Login setIsLoggedIn={setIsLoggedIn}/>}
                    </Route>
                    <Route exact path="/checkout">
                        {!isLoggedIn ? <Redirect to="/login"/> : <Checkout/>}
                    </Route>
                    <Route exact path="/admin">
                        {!isLoggedIn ? <Redirect to="/login"/> : <Admin/>}
                    </Route>
                    <Route exact path="/addcake">
                        {!isLoggedIn ? <Redirect to="/login"/> : <AddCake/>}
                    </Route>
                    {/* <Route exact path="/cakedetails/:id">
                        {!isLoggedIn ? <Redirect to="/login"/> : <CakeDetails/>}
                    </Route> */}
                    <Route exact path="/cakedetails/:id" component={CakeDetails}/>
                    <Route exact path="/cart">
                        {!isLoggedIn ? <Redirect to="/login"/> : <Cart/>}
                    </Route>
                    <Route exact path="/forgot" component={Forgot}></Route>
                    <Route exact path="/*" component={PageNotFound}></Route>
                </Switch>
            </BrowserRouter>
        </div>

    )
}

export default NewApp;
