import axios from "axios";
import { FETCH_LOGIN_REQUEST, FETCH_LOGIN_SUCCESS, FETCH_LOGIN_ERROR, FETCH_USERDATA_REQUEST, FETCH_USERDATA_SUCCESS } from "./loginTypes";

export const fetchLoginRequest = () => {
    return {
        type:FETCH_LOGIN_REQUEST,
    }
};

export const fetchLoginSuccess = payload => {
    console.log(payload)
    return {
        type:FETCH_LOGIN_SUCCESS,
        payload:payload
    }
}

export const fetchLoginError = payload => {
    return {
        type:FETCH_LOGIN_ERROR, 
        payload:payload
    }
}

const fetchUserDataRequest = () => {
    return {
        type:FETCH_USERDATA_REQUEST
    }
}

const fetchUserDataSuccess = payload => {
    return {
        type:FETCH_USERDATA_SUCCESS,
        payload:payload,
    }
}

export const fetchLogin = (email, password) => {
    return (dispatch) => {
        dispatch(fetchLoginRequest())
        axios.post(`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/login`,{email:email, password:password}).then((response) => {
            localStorage.setItem("token",response.data.token)
            dispatch(fetchLoginSuccess(response.data.token))
        }).catch((error) => dispatch(fetchLoginError(error.message)));
    }
}
