import axios from "axios";
import { useState,useEffect } from "react";
import StarRatingComponent from "react-star-rating-component";
import { CircleLoader } from "react-spinners";

const CakeDetails = ({match, location}) => {
    const {params : {id}} = match;
    console.log(match);
    const [cake, setCake] = useState({});
    const [loading, setLoading] = useState(true);
    const cakeUrl = `https://apifromashu.herokuapp.com/api/cake/${id}`;
    console.log("url",cakeUrl);

    useEffect(() => {
        axios({
            method:"GET",
            url:cakeUrl,
        })
        .then((response) => {
            console.log(response.data);
            setCake(response.data.data)
            setLoading(false);
            console.log(cake);
        }, (error) => {
            console.log(error);
        })
    },[]);
    return (
        <div className="row">
            <div className="col-6">
                <img src={cake.image} alt="Cake Image"/>
            </div>
            <div className="col-6">
                <table className="table">
                    <tr>
                        <td>Name: {cake.name}</td>
                    </tr>
                    <tr>
                        <td>Description: {cake.description}</td>
                    </tr>
                    <tr>
                        <td>Flavour: {cake.flavour}</td>
                    </tr>
                    <tr>
                        <td>Cake type: {cake.type}</td>
                    </tr>
                    <tr>
                        <td>Price: {cake.price}</td>
                    </tr>
                    <tr>
                        <td>Created by: {cake.owner?.name}</td>
                    </tr>
                    <tr>
                        <td>Ingredients</td>
                        <td>
                            <ul>
                                {cake.ingredients?.map((each, index) =>{

                                    return(
                                        <li>{each}</li>
                                    )
                                })}
                            </ul>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <StarRatingComponent
                            name="Rating"
                            value="4.5"
                            starCount="5"
                            renderStarIconHalf={() => {
                                return (
                                  <span>
                                    <span style={{position: 'absolute'}}><i className="far fa-star" /></span>
                                    <span><i className="fas fa-star-half" /></span>
                                  </span>
                                );
                              }}
                            />
                        </td>
                    </tr>
                </table>
            </div>
        </div>
    );
};

export default CakeDetails;
