import { Link } from 'react-router-dom';
import {useState} from 'react';
import axios from 'axios';
import { CircleLoader } from 'react-spinners';

function Login({handleLogin}){
    let textData ="";
    let typedPassword ="";
    const [emailError, setEmailError] = useState("")
    const[passWordError, setPasswordError] = useState("");
    const [loading, setLoading] = useState(false);
    const loaderStyle = {
        position:"absolute",
        left:"50%",
        top:"50%",
        zIndex:"999",
    }
    const validateEmail = (email) => {
        const re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(email);
    }
    const login = (event) => {
        setLoading(true);
        let validationError = false;
        event.preventDefault();
        if (textData === "") {
            setEmailError("Email is required");
            if (!validationError) {
                setPasswordError("")
            }
            validationError = true;
        }
        else if (!validateEmail(textData)) {
            setEmailError("Invalid email");
            validationError = true;
        }
        if (!validationError) {
            axios({
                method:"POST",
                url:`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/login`,
                data:{email:textData, password:typedPassword}
            }).then((response) => {
                console.log(response);
                if (response.data.token) {
                    localStorage.setItem('token',response.data.token)
                    handleLogin(true)
                }
                setLoading(false);
            }, (error) => {
                console.log(error);
            })


            // this.props.history.push("/");
        }
    }

    const getTypedEmail = (event) => {
        textData = event.target.value;
    }

    const getTypedPassword = (event) => {
        typedPassword = event.target.value;
    }



    return (
        <div>
            {loading ? <div style={loaderStyle}><CircleLoader/></div>:<div class="container mt-5">
                <div className="form-group">
                    <input onChange={getTypedEmail} type="text"className="form-control" placeholder="Type your email here..."/>
                    <div style={{color:"red",fontSize:"15px"}}>{emailError}</div>
                </div>
                <div className="form-group">
                    <input onChange={getTypedPassword} type="password"className="form-control" placeholder="Type your password here..."/>
                </div>
                <div className="form-group">
                <button onClick={login} className="btn btn-danger">Login</button>
                </div>
                <div className="row">
                    <div className="col-6">
                        <Link to="/forgot">
                            <a className="btn btn-danger" href="#">Forgot password</a>
                        </Link>
                    </div>
                </div>
            </div>}
        </div>



    )
}

export default Login;
