import { Link } from "react-router-dom";
const cake = {
    name:"Chocolate Truffle Cake",
    price:"500",
    image:"truffle.png"
}
function Cake(props) {
    return (
        <div>
            <div className="card" style={{width: "25rem"}}>
                <img src={props.cake.image} className="card-img-top" alt="..." style={{height:"270px",width:"25rem"}}/>
                <div className="card-body">
                    <h5 className="card-title">
                        <Link 
                        to={`cakedetails/${props.cake.cakeid}`} 
                        params={{cakeId:props.cake.cakeid}}
                        >
                            {props.cake.name}
                        </Link>
                    </h5>
                    <p className="card-text">{props.cake.price}</p>
                    <a href="#" className="btn btn-primary btn-block">Add to Cart</a>
                </div>
            </div>
        </div>
    )
}

export default Cake;
