import Cake from "./Cake";
import {useEffect, useState} from 'react';
import axios from "axios";
import { CircleLoader } from "react-spinners";

function CakeList({url}) {
    const [cakes, setCakes] = useState([]);
    const [loading, setLoading] = useState(true);
    const loaderStyle = {
        position:"absolute",
        left:"50%",
        top:"50%",
        zIndex:"999",
    }
    useEffect(() => {
        axios({
            method:"GET",
            url: url || `${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/allcakes`,
        }).then(
            (response) => {
                console.log(response);
                setCakes(response.data.data);
                setLoading(false);
            },
            (error) => {
                console.log(error);
            }
        );
    },[url])

    return (
        <div>
            {loading ? <div style={loaderStyle}><CircleLoader/></div> : <div style={{display:"grid", gridTemplateColumns:"33% 33% 33%",gridGap:"50px"}}>
                    {cakes.map((cake, index) => {
                        return (
                             <Cake cake={cake} key={index}/>
                        )
                    })}
                </div>}
        </div>


    )
}

export default CakeList;
