import axios from "axios";
import { FETCH_LOGIN_REQUEST, FETCH_LOGIN_SUCCESS, FETCH_LOGIN_ERROR } from "./loginTypes";

export const fetchLoginRequest = () => {
    return {
        type:FETCH_LOGIN_REQUEST,
    }
};

export const fetchLoginSuccess = payload => {
    console.log(payload)
    return {
        type:FETCH_LOGIN_SUCCESS,
        payload:payload
    }
}

export const fetchLoginError = payload => {
    return {
        type:FETCH_LOGIN_ERROR, 
        payload:payload
    }
}

export const fetchLogin = (email, password) => {
    console.log(`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/login`)
    return (dispatch) => {
        dispatch(fetchLoginRequest())
        axios({
            method:"POST",
            url:`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/login`,
            data:{email:email, password:password}
        }).then((response) => {
            localStorage.setItem("token",response.data.token)
            dispatch(fetchLoginSuccess(response.data.token))
        }).catch((error) => console.log(error));
    }
}
