import {Component} from 'react';

class Signup extends Component {
    constructor() {
        this.textData = "";
        this.typedPassword = "";
        super();
        this.state = {
            email: {
                value:"",
                message:""
            },
            password: {
                value:"",
                message:""
            }
        };
    }

    validateEmail = (email) => {
        const re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(email);
      }
    subscribeMail = (event) => {
        event.preventDefault();
        if (!this.textData) {
            this.setState({...this.state, email:{value:"", message:"Email is required"}})
        }
        else if (this.validateEmail(this.textData)) {
            this.setState({...this.state, email:{value:this.textData, message:"Email is invalid"}});
        }
        else {
            this.setState(this.setState({email:{value:this.textData, message:""}, password:{value:this.typedPassword, message:""}}));
            console.log({name:this.textData, password:this.typedPassword});
        }
        console.log(this.state)
    }

    getTypedText = (event) => {
        this.textData = event.target.value;
    }

    getTypedPassword = (event) => {
        this.typedPassword = event.target.value;
    }



    render() {
        return (
            <div>
                <div>
                    {this.state.messages}
                </div>
                <div className="form-group">
                    <input onChange={this.getTypedText} type="text"className="form-control" placeholder="Type your email here..."/>
                    <div className="invalid-feedback">{this.state.email.message}</div>
                </div>
                <div className="form-group">
                    <input onChange={this.getTypedPassword} type="password"className="form-control" placeholder="Type your password here..."/>
                    <div className="invalid-feedback">{this.state.password.message}</div>
                </div>
                <div className="form-group">
                    <input type="submit" onClick={this.subscribeMail} value="Subscribe" className="btn btn-danger"/>
                </div>
            </div>
        )
    }
}

export default Signup;
