import {Component} from 'react';

class Signup extends Component {
    constructor() {
        super();
        this.state = {
            messages:""
        };
    }

    validateEmail = (email) => {
        const re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(email);
      }
    subscribeMail = (event) => {
        event.preventDefault();
        if (!this.emailData) {
            this.setState({messages:"Email is required"})
        }
        else if (this.validateEmail(this.emailData)) {
            this.setState({messages:"Email Not Valid"});
        }
        else {
            console.log({name:this.textData, password:this.typedPassword});
        }
    }

    getTypedText = (event) => {
        this.emailData = event.target.value;
    }

    getTypedPassword = (event) => {
        this.typedPassword = event.target.value;
    }



    render() {
        return (
            <div className="row">
                <div>
                    {this.state.messages}
                </div>
                <div className="form-row">
                    <div className="col-4">
                        <input onChange={this.getTypedText} type="text"className="form-control" placeholder="Type your email here..."/>
                    </div>
                    <div className="col-4">
                        <input onChange={this.getTypedPassword} type="text"className="form-control" placeholder="Type your password here..."/>
                    </div>
                    <div className="col">
                        <input type="submit" onClick={this.subscribeMail} value="Subscribe" className="btn btn-danger"/>
                    </div>
                </div>
            </div>
        )
    }
}

export default Signup;
