import { 
    ADD_TO_CART_REQUEST, 
    ADD_TO_CART_SUCCESS, 
    ADD_TO_CART_ERROR,
    FETCH_CART_ERROR, 
    FETCH_CART_REQUEST, 
    FETCH_CART_SUCCESS, 
    REMOVE_CART_ERROR, 
    REMOVE_CART_REQUEST, 
    REMOVE_CART_SUCCESS 
} from "./cartTypes"

const initialState = {
    loading:false,
    cart:[],
    error:'',
    cartCount:0
}

const cartReducer = (state = initialState, action) => {
    switch(action.type) {
        case FETCH_CART_REQUEST:
            return {
                ...state,
                loading:true,
                error:''
            }
        case FETCH_CART_SUCCESS:
            return {
                ...state,
                loading:false,
                cart:action.payload,
                error:'',
            }
        case FETCH_CART_ERROR:
            return {
                ...state,
                loading:false,
                error:action.payload
            }
        case ADD_TO_CART_REQUEST:
            return {
                ...state,
                loading:true,
                error:''
            }
        case ADD_TO_CART_SUCCESS:
            return {
                ...state,
                loading:false,
                error:'',
                cartCount:state.cartCount + 1
            }
        case ADD_TO_CART_ERROR:
            return {
                ...state,
                loading:false,
                error:''
            }
        case REMOVE_CART_REQUEST:
            return {
                ...state,
                loading:true,
                error:''
            }
        case REMOVE_CART_SUCCESS:
            return {
                ...state,
                loading:false,
                cart:cart.filter(item => item.name !== action.payload),
                error:'',
                cartCount: state.cartCount - 1
            }
        case REMOVE_CART_ERROR:
            return {
                ...state,
                loading:false,
                error:''
            }
        case CHECKOUT_CART_REQUEST:
            return {
                ...state,
                loading:true,
                error:''
            }
        case CHECKOUT_CART_SUCCESS:
            return {
                ...state,
                loading:false,
                cart:[],
                error:'',
                cartCount:0
            }
        case CHECKOUT_CART_ERROR:
            return {
                ...state,
                loading:false,
                error:''
            }
        default: return state
    }
}

export default cartReducer;
