import { Link } from "react-router-dom";
import axios from "axios";
import React from "react";
import { useHistory, useLocation } from "react-router";
import { connect } from "react-redux";
import {FaCartPlus} from 'react-icons/fa';

function Navbar(props) {
    const passThrough = false
    const login = true;
    const history = useHistory();
    const location = useLocation();
    let text;
    let isLoggedIn = props.isLoggedIn;
    const search = (event) => {
      event.preventDefault();
      if (text !== "") {
        const route = `/search?q=${text}`
        history.push(route);
      }
    };

    const logout = (event) => {
      event.preventDefault();
      localStorage.clear();
      window.location.reload();
    }

    const getSearchedText = (event) => {
      console.log(event.target.value)
      text = event.target.value;
    }
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <Link to="/">
          <a className="navbar-brand" href="#">Amit Satanic Cakes</a>
        </Link>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            {props.isLoggedIn ? <li className="nav-item active">
              <Link to="/">
                <a className="nav-link" href="#">Home<span className="sr-only">(current)</span></a>
              </Link>
            </li>:<React.Fragment/>}
          </ul>
          <form className="form-inline my-2 mr-10 my-lg-0">
          <input onChange={getSearchedText} className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"/>
          <button onClick={search} className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
          </form>
          <ul className="navbar-nav mr-25">
          {!props.isLoggedIn ? <li className="nav-item">
            <Link to="/login">
              <a className="nav-link btn-danger" href="#">Login</a>
            </Link>
          </li>:<React.Fragment/>}
          {!props.isLoggedIn ? <li className="nav-item">
            <Link to="/signup">
              <a className="nav-link btn-success" href="#">Signup</a>
            </Link>
          </li>:<React.Fragment/>}
          {props.isLoggedIn ? <li className="nav-item">
            <Link to="/cart">
              <a className="nav-link btn-success" href="#"><FaCartPlus/></a>
            </Link>
          </li>:<React.Fragment/>}
          {props.isLoggedIn ? <li className="nav-item">
            <a onClick={logout} className="nav-link btn-danger" href="#">Logout</a>
          </li>:<React.Fragment/>}
          </ul>
        </div>
      </nav>
    )
}

const mapStateToProps = (state) => {
  return {
    isLoggedIn:state.login.isLoggedIn
  }
}

export default connect(mapStateToProps)(Navbar);
