import axios from "axios";
import { useEffect,useState } from "react";
import { CircleLoader } from "react-spinners";
function Cart() {

    const [cart, setCart] = useState([]);
    const [removed, setRemoved] = useState(0);
    const [loading, setLoading] = useState(true)
    const loaderStyle = {
        position:"absolute",
        left:"50%",
        top:"50%",
        zIndex:"999",
    }
    useEffect(() => {
        axios({
            url:`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/cakecart`,
            method:"POST",
            data:{},
        }).then((response) => {
            console.log(response.data);
            setCart(response.data.data);
            setLoading(false);
        }, (error) => {
            console.log(error);
        })

    },[removed]);

    const removeFromCart = (cakeid) => {
        setLoading(true);
        axios({
            url:`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/removecakefromcart`,
            method:"POST",
            data:{cakeid:cakeid},
        }).then((response) => {
            setRemoved(removed + 1);
        }, (error) => {
            console.log(error)
            setLoading(false)
        })
    }

    return (
        <div>
            {loading ? <div style={loaderStyle}><CircleLoader/></div>: <table className="table">
                <thead>
                    <tr>
                        <th>Image</th>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        cart.map((item, index) => {
                            return (
                                <tr key={index}>
                                    <td>
                                        <img 
                                        src={item.image} 
                                        alt="cake img" 
                                        style={{width:"60px",height:"60px"}}
                                        />
                                    </td>
                                    <td>{item.name}</td>
                                    <td>${item.price}</td>
                                    <td>{item.quantity}</td>
                                    <td>
                                        <button 
                                        className="btn btn-danger" 
                                        onClick={() => removeFromCart(item.cakeid)}
                                        >
                                            Remove from cart
                                        </button>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>}
        </div>
    )
}

export default Cart;
