import axios from "axios"
import { 
    CHECKOUT_CART_ERROR, 
    CHECKOUT_CART_REQUEST, 
    CHECKOUT_CART_SUCCESS, 
    FETCH_CART_ERROR, FETCH_CART_REQUEST, 
    FETCH_CART_SUCCESS, 
    REMOVE_CART_ERROR, 
    REMOVE_CART_REQUEST, 
    REMOVE_CART_SUCCESS,
    ADD_TO_CART_REQUEST,
    ADD_TO_CART_SUCCESS,
    ADD_TO_CART_ERROR, 
    REMOVE_ONE_FROM_CART_REQUEST,
    REMOVE_ONE_FROM_CART_SUCCESS,
    REMOVE_ONE_FROM_CART_ERROR
} from "./cartTypes"

export const fetchCartRequest = () => {
    return {
        type:FETCH_CART_REQUEST
    }
}

export const fetchCartSuccess = payload => {
    return {
        type:FETCH_CART_SUCCESS,
        payload:payload
    }
}

export const fetchCartError = payload => {
    return {
        type:FETCH_CART_ERROR,
        payload:payload
    }
}

export const removeCartRequest = () => {
    return {
        type:REMOVE_CART_REQUEST
    }
}

export const removeCartSuccess = (payload) => {
    return {
        type: REMOVE_CART_SUCCESS,
        payload:payload
    }
}

export const removeCartError = payload => {
    return {
        type:REMOVE_CART_ERROR,
        payload:payload
    }
}

export const checkOutCartRequest = () => {
    return {
        type:CHECKOUT_CART_REQUEST,
    }
}

export const checkOutCartSuccess = () => {
    return {
        type:CHECKOUT_CART_SUCCESS,
    }
}

export const checkOutCartError = payload => {
    return {
        type:CHECKOUT_CART_ERROR,
        payload:payload
    }
}

export const addToCartRequest = () => {
    return {
        type:ADD_TO_CART_REQUEST,
    }
}

export const addToCartSuccess = () => {
    return {
        type:ADD_TO_CART_SUCCESS,
    }
}

export const addToCartError = payload => {
    return {
        type:ADD_TO_CART_ERROR,
        payload:payload
    }
}

export const removeOneFromCartRequest = () => {
    return {
        type:REMOVE_ONE_FROM_CART_REQUEST
    }
}

export const removeOneFromCartSuccess = (payload) => {
    return {
        type: REMOVE_ONE_FROM_CART_SUCCESS,
        payload:payload
    }
}

export const removeOneFromCartError = payload => {
    return {
        type:REMOVE_ONE_FROM_CART_ERROR,
        payload:payload
    }
}

export const fetchCart = () => {
    return (dispatch) => {
        dispatch(fetchCartRequest());
        axios.post(`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/cakecart`, {}).then(
            response => dispatch(fetchCartSuccess(response.data.data))
        ).catch(
            error => dispatch(fetchCartSuccess(error.message))
        )
    }
}

export const removeFromCart = (cakeid) => {
    return (dispatch) => {
        dispatch(removeCartRequest());
        axios.post(`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/removecakefromcart`, {
            cakeid:cakeid
        }).then(
            response => dispatch(removeCartSuccess(cakeid))
        ).catch(
            error => dispatch(removeCartError(error.message))
        )
    }
}

export const checkoutCart = () => {
    return (dispatch) => {
        dispatch(checkOutCartRequest());
        axios.post(`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/checkout`, {}).then(
            response => dispatch(checkOutCartSuccess(response.data.data))
        ).catch(
            error => dispatch(checkOutCartError(error.message))
        )
    }
}

export const addToCart = (data) => {
    console.log(data)
    return dispatch => {
        dispatch(addToCartRequest())
        const postData = {
            name:data.name,
            cakeid:data.cakeid,
            price:data.price,
            weight: data.weight,
            image:data.image,
            quantity:1
        };
        const url = `${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/addcaketocart`
        axios.post(url, postData).then(
            response => {
                alert("Product added to cart successfully")
                dispatch(addToCartSuccess())
            }
        ).catch(
            error => dispatch(addToCartError(error.message))
        )
    }

}

export const removeOneFromCart = (data) => {
    return dispatch => {
        dispatch(removeOneFromCartRequest())
        axios.post(`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/removeonecakefromcart`,{
            cakeid:data
        }).then(
            response => dispatch(removeOneFromCartSuccess(data))
        ).catch(
            error => dispatch(removeOneFromCartError(error.message))
        )
    }
}
