import { Link } from 'react-router-dom';
import {useState} from 'react';
import axios from 'axios';

function Login(){
    let textData ="";
    let typedPassword ="";
    const [error, setError] = useState(null)
    const [passwordError, setPasswordError] = useState(null)
    const validateEmail = (email) => {
        const re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(email);
      }
    const login = (event) => {
        let error = false;
        event.preventDefault();
        let currState = {...this.state};
        if (this.textData === "") {
            currState.email.message = "Email is required"
            this.setState({currState});
            if (!error) {
                currState.password.message = "";
            }
            error = true;
        }
        else if (!this.validateEmail(this.textData)) {
            let currState = {...this.state};
            currState.email.message = "Email is invalid"
            this.setState({currState});
            if (!error) {
                currState.password.message = "";
                currState.name.message = "";
            }
            error = true;
        }
        if (!error) {
            axios({
                method:"POST",
                url:"https://apifromashu.herokuapp.com/api/register"
            })
            this.setState(this.setState({email:{value:this.textData, message:""}, password:{value:this.typedPassword, message:""}}));
            console.log(this.state);
            this.props.history.push("/");
        }
    }

    const getTypedEmail = (event) => {
        textData = event.target.value;
    }

    const getTypedPassword = (event) => {
        typedPassword = event.target.value;
    }



    return (
        <div>
            <div className="form-group">
                <input onChange={getTypedEmail} type="text"className="form-control" placeholder="Type your email here..."/>
                <div style={{color:"red",fontSize:"15px"}}>{error}</div>
            </div>
            <div className="form-group">
                <input onChange={getTypedPassword} type="password"className="form-control" placeholder="Type your password here..."/>
                <div style={{color:"red",fontSize:"15px"}}>{error}</div>
            </div>
            <div className="form-group">
            <button onClick={login} className="btn btn-danger">Login</button>
            </div>
            <div className="row">
                <div className="col-6">
                    <Link to="/forgot">
                        <a className="btn btn-danger" href="#">Forgot password</a>
                    </Link>
                </div>
            </div>

        </div>
    )
}

export default Login;
