import { Link } from "react-router-dom";
import axios from "axios";
import { connect } from "react-redux";
const cake = {
    name:"Chocolate Truffle Cake",
    price:"500",
    image:"truffle.png"
}
function Cake(props) {
    const addToCart = () => {
        axios({
            url:`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/addcaketocart`,
            method:"POST",
            data:{
                name:props.cake.name,
                cakeid:props.cake.cakeid,
                price:props.cake.price,
                weight: props.cake.weight,
                image:props.cake.image,
                quantity:1
            }
        }).then((response) => {
            alert("Product added to cart successfully");
            console.log(response.data);
        }, (error) => {
            console.log(error);
        })
    }
    return (
        <div>
            <div className="card" style={{width: "32rem"}}>
                <img src={props.cake.image} className="card-img-top" alt="..." style={{height:"270px",width:"32rem"}}/>
                <div className="card-body">
                    <h5 className="card-title">
                        <Link 
                        to={`cakedetails/${props.cake.cakeid}`} 
                        params={{cakeId:props.cake.cakeid}}
                        >
                            {props.cake.name}
                        </Link>
                    </h5>
                    <p className="card-text">{props.cake.price}</p>
                    <a 
                    href="#"
                    onClick={addToCart} 
                    className="btn btn-primary btn-block">
                    Add to Cart
                    </a>
                </div>
            </div>
        </div>
    )
}

export default Cake;
