import { Link } from "react-router-dom";
import axios from "axios";
import React from "react";
function Navbar({isLoggedIn, setUrl}) {
    const passThrough = false
    const login = true;
    let text;
    const search = (event) => {
      event.preventDefault();
      if (text !== "") {
        setUrl(`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/searchcakes?q=${text}`);
      }
      else {
        setUrl(`${process.env.REACT_APP_CAKE_WEBSITE_API_URL}/allcakes`)
      }
    };

    const getSearchedText = (event) => {
      console.log(event.target.value)
      text = event.target.value;
    }
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <Link to="/">
          <a className="navbar-brand" href="#">Amit Satanic Cakes</a>
        </Link>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            {isLoggedIn ? <li className="nav-item active">
              <Link to="/">
                <a className="nav-link" href="#">Home<span className="sr-only">(current)</span></a>
              </Link>
            </li>:<React.Fragment/>}
          </ul>
          <ul className="navbar-nav mr-25">
          {!isLoggedIn ? <li className="nav-item">
            <Link to="/login">
              <a className="nav-link" href="#">Login</a>
            </Link>
          </li>:<React.Fragment/>}
          {!isLoggedIn ? <li className="nav-item">
            <Link to="/signup">
              <a className="nav-link" href="#">Signup</a>
            </Link>
          </li>:<React.Fragment/>}
          </ul>
          <form className="form-inline my-2 my-lg-0">
          <input onChange={getSearchedText} className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"/>
          <button onClick={search} className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
          </form>
        </div>
      </nav>
    )
}

export default Navbar;
