import { 
    ADD_TO_CART_REQUEST, 
    ADD_TO_CART_SUCCESS, 
    ADD_TO_CART_ERROR,  
    FETCH_CART_ERROR, 
    FETCH_CART_REQUEST, 
    FETCH_CART_SUCCESS, 
    REMOVE_CART_ERROR, 
    REMOVE_CART_REQUEST, 
    REMOVE_CART_SUCCESS,
    CHECKOUT_CART_REQUEST,
    CHECKOUT_CART_SUCCESS,
    CHECKOUT_CART_ERROR 
} from "./cartTypes"

const initialState = {
    loading:false,
    cart:[],
    error:'',
    cartCount:0
}

const cartReducer = (state = initialState, action) => {
    switch(action.type) {
        case FETCH_CART_REQUEST:
            return {
                ...state,
                loading:true,
                error:''
            }
        case FETCH_CART_SUCCESS:
            return {
                ...state,
                loading:false,
                cart:action.payload,
                error:'',
                cartCount:getCartCount(action.payload)
            }
        case FETCH_CART_ERROR:
            return {
                ...state,
                loading:false,
                error:action.payload
            }
        case ADD_TO_CART_REQUEST:
            return {
                ...state,
                loading:true,
                error:''
            }
        case ADD_TO_CART_SUCCESS:
            return {
                ...state,
                loading:false,
                error:'',
                cartCount:state.cartCount + 1
            }
        case ADD_TO_CART_ERROR:
            return {
                ...state,
                loading:false,
                error:''
            }
        case REMOVE_CART_REQUEST:
            return {
                ...state,
                loading:true,
                error:''
            }
        case REMOVE_CART_SUCCESS:
            return {
                ...state,
                loading:false,
                cart:state.cart.filter(item => item.cakeid !== action.payload),
                error:'',
                cartCount: state.cartCount - 1
            }
        case REMOVE_CART_ERROR:
            return {
                ...state,
                loading:false,
                error:''
            }
        case CHECKOUT_CART_REQUEST:
            return {
                ...state,
                loading:true,
                error:''
            }
        case CHECKOUT_CART_SUCCESS:
            return {
                ...state,
                loading:false,
                cart:[],
                error:'',
                cartCount:0
            }
        case CHECKOUT_CART_ERROR:
            return {
                ...state,
                loading:false,
                error:''
            }
        default: return state
    }
}

const getCartCount = (cart) => {
    let cartCount = 0;
    cart.forEach(item => {
        cartCount += item.quantity
    })
    return cartCount
}

const removeCartCount = (cart, cartCount, cakeid) => {
    const item = cart.filter(item => item.cakeid === cakeid)
}

export default cartReducer;
